  background-color: #444;
  flex: 1;
  align-items: center;
  justify-content: center;
`

const InputLabel = styled.Text`
  color: #fff;  
  font-size: 22px;
`

const Input = styled.TextInput`
  width: 90%;
  height: 40px;
  margin-top: 10px;
  padding: 10px;
  background-color: #ccc;
  border-radius: 8px;
`

const Button = styled.TouchableOpacity`
  width: 90%;
  height: 40px;
  margin-top:20px;
  justify-content: center;
  align-items: center;
  background-color: #ffa100;
  border-radius: 8px;
  margin-bottom: 40px;
`

const ButtonText = styled.Text`
  font-size: 20px;
  font-weight: bold;
  color: #fff;
`

const CuponArea = styled.View`
  padding: 30px;
  background-color: #fff;
  align-items: center;
  border-radius: 5px;
`

